     */
    public function addDetected($signature_id, $snippet, $table_name, $row_id, $field = '', $field_data = [])
    {
        $this->addSignatureRowId($signature_id, $snippet, $table_name, $row_id, $field, $field_data, self::STATUS_DETECTED, $this->report_scan);
    }

    /**
     * Add detected info
